CREATE TABLE locations (
    location_id INT AUTO_INCREMENT PRIMARY KEY,
    location_name VARCHAR(255) NOT NULL,
    region VARCHAR(255),
    description TEXT 
);
CREATE TABLE _events (
    event_id INT AUTO_INCREMENT PRIMARY KEY,
    event_name VARCHAR(255) NOT NULL,
    event_date DATE,
    description TEXT,
    location_id INT,
    FOREIGN KEY (location_id) REFERENCES locations(location_id)
);
CREATE TABLE battles (
    battle_id INT AUTO_INCREMENT PRIMARY KEY,
    battle_name VARCHAR(255) NOT NULL,
    battle_date DATE,
    description TEXT,
    location_id INT,
    FOREIGN KEY (location_id) REFERENCES locations(location_id)
);
CREATE TABLE conquests (
    conquest_id INT AUTO_INCREMENT PRIMARY KEY,
    conquest_name VARCHAR(255) NOT NULL,
    conquest_date DATE,
    description TEXT,
    location_id INT,
    FOREIGN KEY (location_id) REFERENCES locations(location_id)
);
CREATE TABLE persons (
    person_id INT AUTO_INCREMENT PRIMARY KEY,
    first_name VARCHAR(100) NOT NULL,
    last_name VARCHAR(100) NOT NULL,
    birth_date DATE,
    death_date DATE,
    role VARCHAR(255),
    bio TEXT
);
CREATE TABLE battles_loctions_relation (
    battle_id INT,
    location_id INT,
    PRIMARY KEY (battle_id, location_id),
    FOREIGN KEY (battle_id) REFERENCES battles(battle_id),
    FOREIGN KEY (location_id) REFERENCES locations(location_id)
);
CREATE TABLE battle_person_relation (
    battle_id INT,
    person_id INT,
    role_in_battle VARCHAR(255),
    PRIMARY KEY (battle_id, person_id),
    FOREIGN KEY (battle_id) REFERENCES battles(battle_id),
    FOREIGN KEY (person_id) REFERENCES persons(person_id)
);
CREATE TABLE conquest_person_relation (
    conquest_id INT,
    person_id INT,
    role_in_conquest VARCHAR(255),
    PRIMARY KEY (conquest_id, person_id),
    FOREIGN KEY (conquest_id) REFERENCES conquests(conquest_id),
    FOREIGN KEY (person_id) REFERENCES persons(person_id)
);
create table timeline (
timeline_id int primary key,
full_details text
);

select * from conquests;
select * from persons;

