INSERT INTO persons (person_id, first_name, last_name, birth_date, death_date, _role, bio, full_details) 
VALUES (1, 'موسى بن نصير', 'موسى بن نصير اللخمي ', 19, 97, 'موسى بن نصير كان قائدًا عسكريًا وواليًا على إفريقية في الدولة الأموية', 'تولى ولاية إفريقية ونجح في استكمال فتحها، كما كان له دور كبير في فتح الأندلس بعد أن أرسل القائد طارق بن زياد إليها. عُرف بحكمته وبراعته في الإدارة والقيادة، وساهم في نشر الإسلام وتعزيز الاستقرار في المناطق التي فتحها', 'موسى بن نصير هو القائد البارع والتقي، كان موسى بن نصير قائدا فذا، تقيا ورعاء أرسى الله به دعائم الإسلام في هذه المنطقة الواسعة. وهو من التابعين، وروى عن بعض الصحابة، ومنهم تميم الداري.'),
 (2, 'طارق بن زياد ', 'طارق بن زياد الليثي', 50, 102, 'قائد عسكري في جيش الدولة الأموية، وهو الذي قاد الفتح الإسلامي للأندلس عام 92 هـ / 711 م، تحت قيادة والي إفريقية موسى بن نصير', 'طارق بن زياد هو أحد أعظم القادة في التاريخ الإسلامي، اشتهر بفتحه للأندلس سنة 711م، وقد عُرف بالشجاعة والذكاء العسكري. يُنسب إليه الخطاب الشهير الذي حفّز فيه جيشه بعد عبور البحر، ويعدّ من رموز الفتح الإسلامي في أوروبا.', 'كان طارق بن زياد قائدًا يجمع بين التقوى الكفاءة العسكرية، وحب الجهاد، مع رغبة صادقة في الاستشهاد في سبيل الله. ورغم كونه أمازيغيا (بربريا) وليس عربيا، فضله موسى بن نصير على غيره من القادة العرب لعدة أسباب:');

