INSERT INTO persons (
    person_id, first_name, last_name, birth_date, death_date, _role, bio, full_details
)
VALUES
(10, 'محمد بن أبي عامر (الحاجب المنصور)','أبو عامر محمد بن عبد الله بن محمد بن عبد الله بن عامر بن الوليد المعافري القرطبي', 938, 1002, 'تولى منصب الحاجب (أي رئيس الوزراء أو الحاكم الفعلي) في الدولة العامرية بالأندلس', 'كان من أقوى الشخصيات في تاريخ الأندلس. جمع بين الذكاء السياسي والدهاء العسكري، ونجح في تثبيت الأمن الداخلي وتوسيع حدود الدولة الإسلامية في الأندلس. يُعتبر عصره من أكثر العصور استقرارًا في تاريخ الأندلس', 'قائد عسكري وسياسي بارع، عزز نفوذ العامريين في الأندلس وقاد العديد من الغزوات الناجحة ضد النصارى، واشتهر المنصور بن أبي عامر بحزمه وعدله وشجاعته. من أبرز مواقفه أنه أرسل جيشًا جرارًا لإنقاذ ثلاث نساء مسلمات كنّ أسيرات في مملكة نافار، رغم أن هذه المملكة كانت تدفع الجزية للمسلمين. كما أنه عندما حاصره النصارى في ممر جبلي، لم يستسلم، بل احتل مدينة مسيحية، وحوّلها إلى قاعدة عسكرية، وأجبرهم على التفاوض معه بشروطه، ليعود منتصرًا بالغنائم. وكان يجمع غبار معاركه ليُدفن معه، معتقدًا أنه شهادة لجهاده في سبيل الله، برز كقائد عسكري وإداري محنك، استغل الأوضاع السياسية لصالحه، ونجح في استمالة غالب الناصري، بل وتزوج ابنته، مما عزز نفوذه أكثر، كما كان من أبرز الشخصيات التي أثرت في تاريخ الأندلس، فقد أدار شؤون الحكم بحنكة جعلته يسيطر تدريجيًا على مقاليد السلطة، وبفضل ذكائه وطموحه، تولى عدة مناصب مهمة، منها القضاء، وإدارة الشرطة، والإشراف على الخزانة العامة، ثم أصبح الحاجب الفعلي للخلافة، وتميّز بحسن التدبير والقدرة على توظيف الأحداث لصالحه، كما استغل علاقته الوثيقة بصبح البشكنسية، أم الخليفة هشام، لتعزيز نفوذه داخل القصر، وتميز بذكائه السياسي، فأقصى بني أمية والمصحفيين، واعتمد على البربر لضمان سلطته، وأنشأ مدينة الزاهرة عام 368هـ، لتكون عاصمة حكمه بعيدًا عن مؤامرات قرطبة، كما عزز نفوذه بتعيين ابنه عبد الملك وريثًا للحجابة عام 381هـ، مما كان سابقة في التاريخ الإسلامي'),
(11, 'غالب الناصري', 'أبو تمام غالب بن عبد الرحمن الناصري', null, 974, 'كان من أعظم قادة الجيش العامري في الأندلس', 'قائد عسكري بارز في الأندلس، لُقب بـ"الفارس الذي لم يُهزم، وكان له هيبة كبرى بين الجيوش. انتهت حياته في صراع على السلطة مع الحاجب المنصور',  'أحد أعظم قادة الأندلس العسكريين، تحالف مع ابن أبي عامر ضد المصحفي، ودعمه ليصبح الحاجب الفعلي للخلافة، وخاض العديد من المعارك ضد الممالك المسيحية، مثل ليون ونافارا، وحقق انتصارات كبيرة عززت هيبة الدولة الأموية، وساعد الملك سانشو الأول ملك ليون على استعادة عرشه عام 960م بتكليف من الخليفة عبد الرحمن الناصر، ورغم خدمته الطويلة، دخل في صراع مع الحاجب المنصور الذي استحوذ على السلطة بعد وفاة الحكم المستنصر بالله، وفي أواخر حياته، تمرّد ضد الحاجب المنصور، لكنه هُزم في معركة عام 1002م، وتوفي في ظروف غامضة بعد المعركة'),
(12, 'عبد الملك بن المنصور( الحاجب المظفر)','أبو مروان عبد الملك بن محمد بن أبي عامر المعافري المعروف بلقب الحاجب المظفر بالله', 974, 1009, 'كان حاجبا رئيس الوزراء للخليفة هشام المؤيد بالله', 'كان حاكما كفوا، حافظ على استقرار الدولة بعد وفاةوالده، وتوفي في أوج قوته، مما أدى إلى بداية الفتن في الأندلس', 'هو عبد الملك بن محمد بن أبي عامر، المعروف بابن المنصور، وهو الحاكم الفعلي للأندلس بين 1002م و1008م خلال عصر الخلافة العامرية. تولى السلطة بعد وفاة والده الحاجب المنصور عام 1002م، لكنه لم يحمل لقب الخليفة، بل كان الحاجب والحاكم الفعلي باسم الخليفة الأموي هشام الثاني، وبعد وفاة والده المنصور، ورث الحكم وحافظ على هيمنة أسرة العامريين على الخلافة الأموية، واستمر في إبقاء الخليفة هشام الثاني مجرد رمز دون سلطة حقيقية، كما قاد عدة حملات عسكرية ناجحة ضد الممالك المسيحية في شمال إسبانيا، لكنه لم يكن بنفس قوة والده، وحافظ على الهيمنة الأندلسية على الممالك النصرانية واستمر في فرض الجزية، وواصل سياسة والده في تقوية الجيش والاعتماد على المرتزقة الصقالبة والبربر، وحافظ على استقرار الأندلس نسبيًا خلال فترة حكمه القصي، وتوفي عام 1009م في ظروف غامضة، ويُقال إن وفاته كانت طبيعية، لكن البعض يرى أن هناك صراعًا داخليًا أدى إلى التخلص منه، كما خلفه أخوه عبد الرحمن شنجول، الذي حاول الاستئثار بالحكم وإعلان نفسه خليفة، مما أدى إلى انهيار حكم العامريين واندلاع الفتنة في الأندلس'),
(13, 'عبد الرحمن بن المنصور (عبد الرحمن شنجول)', 'أبو مروان عبد الرحمن بن محمد بن أبي عامر المعافري المعروف بلقب عبد الرحمن شنجول', null, 1009, 'تولى الحجابة بعد وفاة أخيه عبد الملك المظفر، وسعى لتوريث الحكم لنفسه، مما أثار استياء العامة والنخبة السياسية', 'كان آخر من تولى الحجابة من أبناء المنصور، وساهمت سياساته في تفكك الدولة العامرية وسقوط الخلافة الأموية في الأندلس', 'كان آخر حكام الدولة العامرية وابن الحاجب المنصور محمد بن أبي عامر، الذي أسس عصر الخلافة العامرية، حيث حكم الأندلس فعليًا باسم الخليفة الأموي هشام الثاني، وقرر شنجول إجبار الخليفة هشام الثاني على تسميته وليًا للعهد، وهو ما أثار غضب الأندلسيين، وهذه الخطوة كانت أول مرة يُنقل الحكم من الأمويين إلى أسرة أخرى، مما تسبب في سخط واسع، واندلعت ثورة قادها محمد بن هشام المهدي بالله، حفيد عبد الرحمن الناصر، بدعم من القادة العسكريين، واضطر شنجول إلى الفرار لكنه قُتل في الطريق، وسقطت الدولة العامرية، مما أدى إلى بداية الفتنة الكبرى (1009-1031م)، التي أنهت الخلافة الأموية في الأندلس، ووبعد مقتل شنجول، دخلت الأندلس في اضطرابات دمرت وحدة الدولة، وأدت إلى نهاية الخلافة الأموية عام 1031م، وظهور ملوك الطوائف');


-- el2mara elamawiya
